import React, { useState } from 'react';



const UseStateBasics = () => {

  const [text,setText]= useState('random title');
  const handleClick=()=>{
    setText('hello');
  };
  return (
    <React.Fragment> 
      <h1>{text}</h1>
      <button className='btn' onClick={handleClick}>
      change tilte
      </button>
    </React.Fragment>
 );
};

export default UseStateBasics;
