import React, { useState, useEffect } from "react";
import "./style.css";

const API = {
  key: "8d8a7e300ffe83bbdd8278f8c4cf4ad0",
  base: "https://api.openweathermap.org/data/2.5/",
};

const TEMP_THRESHOLD = 20;

function WeatherApp() {
  const [query, setQuery] = useState("");
  const [weather, setWeather] = useState({});

  function capitalizeFirstLetter(string) {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }

  const search = (e) => {
    if (e.key === "Enter") {
      fetch(
        `${API.base}weather?q=${query}&lang=vi&units=metric&APPID=${API.key}`
      )
        .then((res) => res.json())
        .then((re) => {
          setQuery("");
          setWeather(re);
          console.log(re.weather);
        });
    }
  };

  const dateBuilder = (d) => {
    let months = [
      "Tháng một",
      "Tháng hai",
      "Tháng ba",
      "Tháng tư",
      "Tháng năm",
      "Tháng sáu",
      "Tháng bảy",
      "Tháng tám",
      "Tháng chín",
      "Tháng mười",
      "Tháng mười một",
      "Tháng mười hai",
    ];
    let days = [
      "Chủ nhật",
      "Thứ hai",
      "Thứ ba",
      "Thứ tư",
      "Thứ năm",
      "Thứ sáu",
      "Thứ bảy",
    ];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day}, ${date} ${month} ${year}`;
  };

  return (
    <div
      className={
        typeof weather.main != "undefined"
          ? weather.main.temp > TEMP_THRESHOLD
            ? "app warm"
            : "app"
          : "app"
      }
    >
      <main>
        <div className="container">
          <div className="search-box">
            <input
              type="text"
              className="search-bar"
              placeholder="Nhập địa điểm..."
              onChange={(e) => setQuery(e.target.value)}
              value={query}
              onKeyPress={search}
            />
          </div>

          {typeof weather.main != "undefined" ? (
            <>
              <div className="location-box">
                <div className="location">
                  {weather.name},{weather.sys.country}
                </div>
                <div className="date">{dateBuilder(new Date())}</div>
              </div>
              <div className="weather-box">
                <div className="temp">{Math.round(weather.main.temp)}°C</div>
                <div className="weather">
                  {capitalizeFirstLetter(weather.weather[0].description)}
                </div>
              </div>
            </>
          ) : (
            <div className="weather-box">
              <div className="weather">Nhập địa điểm</div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default WeatherApp;
