import React from "react";
import "./style.css";

const api = {
  key: "7d4ef5d23648c7c62acd3ccfa1a0a30b",
  base: "https://api.openweathermap.org/data/2.5/",
};

function WeatherApp() {
  const dateBuilder = (d) => {
    let months = [
      "Tháng một",
      "Tháng hai",
      "Tháng ba",
      "Tháng tư",
      "Tháng năm",
      "Tháng sáu",
      "Tháng bảy",
      "Tháng tám",
      "Tháng chín",
      "Tháng mười",
      "Tháng mười một",
      "Tháng mười hai",
    ];
    let days = [];
  };

  return (
    <div className="app">
      <main>
        <div className="container">
          <div className="search-box">
            <input
              type="text"
              className="search-bar"
              placeholder="Tìm kiếm..."
            />
          </div>
        </div>
      </main>
    </div>
  );
}

export default WeatherApp;
